/** @jsx jsx */
import { jsx, Box, Container, Image, Text } from "theme-ui";
// import { Link } from "components/link";
import { Link } from "react-scroll";
import data from "./footer.data";
import FooterLogo from "assets/logo.svg";

export default function Footer() {
  return (
    <footer sx={styles.footer}>
      <Container>
        <Box sx={styles.footer.footerBottomArea}>
          <Link path="home" >
            <Image src={FooterLogo} alt="footer-logo" sx={styles.footer.homeLink}/>
          </Link>
          <Box sx={styles.footer.menus}>
            <nav>
              {data.menuItem.map((item, index) => {
                const { path, label } = item;
                return (
                  <Link
                  // activeClass="active"
                  to={path}
                  spy={true}
                  smooth={true}
                  // offset={-70}
                  duration={1500}
                  key={index}
                   sx={styles.footer.link}
                >
                  {label}
                </Link>
                );
              })}
            </nav>
          </Box>
          <Text sx={styles.footer.copyright}>
            <span>&#169;</span> {new Date().getFullYear()}
          </Text>
        </Box>
      </Container>
    </footer>
  );
}

const styles = {
  footer: {
    footerBottomArea: {
      borderTop: "1px solid",
      borderTopColor: "border_color",
      display: "flex",
      pt: [7, null, 8],
      pb: ["40px", null, "100px"],
      textAlign: "center",
      flexDirection: "column",
    },
    menus: {
      mt: [3, 4],
      mb: 2,
      nav: {
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexWrap: "wrap",
      },
    },

    link: {
      fontSize: [1, "15px"],
      color: "text",
      fontWeight: "400",
      mb: 2,
      cursor: "pointer",
      transition: "all 0.35s",
      display: "block",
      textDecoration: "none",
      lineHeight: [1.5, null, 1.8],
      px: [2, null, 4],
      ":hover": {
        color: "primary",
      },
    },
    copyright: {
      fontSize: [1, "15px"],
      width: "100%",
    },
    homeLink:{
      cursor:"pointer"
    }
  },
};
